function plotErr(path)    data=csvread(path);    data(:,4:6) = data(:,4:6);    % 读取第1，2，3列，    diff_vel1 = data(:,1);    diff_vel2 = data(:,2);    diff_vel3 = data(:,3);        diff_vel_v =[];        for i = 1:length(diff_vel1)        norm = sqrt(diff_vel1(i)*diff_vel1(i) + diff_vel2(i)*diff_vel2(i)+ diff_vel3(i)*diff_vel3(i));        diff_vel_v = [diff_vel_v, norm];    end        % 读取第4，5，6列，    Twist1 = data(:,4);    Twist2 = data(:,5);    Twist3 = data(:,6);        Twist_v =[];        for i = 1:length(Twist1)        norm = sqrt(Twist1(i)*Twist1(i) + Twist2(i)*Twist2(i)+ Twist3(i)*Twist3(i));        Twist_v = [Twist_v, norm];    end        diff_vel_v(1) = diff_vel_v(2);    figure;    yyaxis left;    hold on;    plot(diff_vel_v,'LineWidth',1.0);    ylabel('Position Error(mm)');        yyaxis right;    hold on    plot(Twist_v,'LineWidth',1.0);        legend('position err', 'rotation err');    title('The Position & Rotation Error');    ylabel('Rotation Error(deg)');    grid onend